/** script to handle the lights off feature,
 * works for video embed in a div(or any element) having "video-js" as one of it's class name, 
 * trying to see if it'll be necessary to detect if its an iframe, or directly a video
 * Iframes are used when the video is from stuff like youtube, etc.
 * For now, its only working for the native links, if you get videos from youtube src,vimeo,etc, it loads into an iframe, so the effect wont working
 * still trying to sort that out, but quite busy. :0
 * @author Precious Omonzejele <omonze@peepsipi.com>
 */
var $=jQuery;var bzpshadowId="bzp-pk-shadow";var bzpvidClass='';var bzpvidzindex='';$(document).ready(function(){$('body').prepend('<div id="'+bzpshadowId+'"></div>');$("#"+bzpshadowId).css("height",$(document).height()).hide();function bzppkturnOffTheLights(_element){var _thevid=$(_element);var _awonId=_thevid.closest('.video-js').attr('id');_thevid.closest('#'+_awonId).css('z-index','2001');$('#'+bzpshadowId).fadeIn()}
function bzppkturnOnTheLights(_element){var _thevid=$(_element);var _awonId=_thevid.closest('.video-js').attr('id');$('#'+bzpshadowId).fadeOut();_thevid.closest('#'+_awonId).css('z-index',bzpvidzindex)}
function bzpnativeVidExec(){var theRand=Math.floor((Math.random()*400)+1);bzpvidClass="bzp-pk-vid-"+theRand;bzpvidzindex=$('.video-js').css('z-index');$('.video-js video').addClass(bzpvidClass);var vidItself=$('.'+bzpvidClass);vidItself.on('playing',function(){bzppkturnOffTheLights(this)});vidItself.on('pause',function(){bzppkturnOnTheLights(this)});vidItself.on('ended',function(){bzppkturnOnTheLights(this)})}
if($('.video-js').hasClass('vjs-youtube')){alert('Sorry, the effect for youtube embeded videos isn\'t supported yet :( ')}
else{bzpnativeVidExec()}})